package com.misit.abpenergy.Apiimport com.misit.abpenergy.Inspeksi.Adapter.InspeksiPicaDetailimport com.misit.abpenergy.Inspeksi.Response.DataInspeksiResponseimport com.misit.abpenergy.Inspeksi.Response.ItemTempResponseimport com.misit.abpenergy.Inspeksi.Response.TeamDetailResponseimport com.misit.abpenergy.Inspeksi.Response.TeamInspeksiTempResponseimport com.misit.abpenergy.Login.Response.CompanyResponseimport com.misit.abpenergy.Login.Response.DaftarAkunResponseimport com.misit.abpenergy.Login.Response.DepartmentResponseimport okhttp3.RequestBodyimport retrofit2.Callimport retrofit2.http.*interface ApiEndPointTwo {    @GET("/android/api/get/perusahaan")    fun getCompany()            : Call<CompanyResponse>?    @GET("/android/api/get/department")    fun getDepartment(@Query("company") company:String)            : Call<DepartmentResponse>?    @FormUrlEncoded    @POST("/android/api/daftar/akun/baru/mitra")    fun daftarkanAkunMitra(@Field("nik") nik:String?,                            @Field("username") username:String?,                            @Field("password") password:String?,                            @Field("nama") nama:String?,                            @Field("email") email:String?,                            @Field("perusahaan") perusahaan:String?,                            @Field("departemen") departemen:String?,                            @Field("devisi") devisi:String?,                            @Field("_token") csrf_token:String?                        ) : Call<DaftarAkunResponse>?    @Multipart    @POST("/hse/android/inspeksi/new/submit")    fun inspeksiSave(        @Part("idTemp") idTemp: RequestBody?,        @Part("idForm") idForm: RequestBody?,        @Part("user") user: RequestBody?,        @Part("tglInspeksi") tglInspeksi: RequestBody?,        @Part("perusahaan") perusahaan: RequestBody?,        @Part("lokasi") lokasi: RequestBody?,        @Part("saran") saran: RequestBody?,        @Part("_token") csrf_token: RequestBody?    )            : Call<ItemTempResponse>?    @GET("/hse/android/inspeksi/list/user")    fun getInspeksiUser(@Query("userInput") userInput:String,                        @Query("idForm") idForm:String)            : Call<DataInspeksiResponse>?    @GET("/hse/android/inspeksi/pica/temp")    fun listInspeksiPica(@Query("idInspeksi") idInspeksi:String?)            : Call<InspeksiPicaDetail>?    @GET("/hse/android/inspeksi/list/team")    fun teamInspeksi(@Query("idInspeksi") idInspeksi:String?)            : Call<TeamDetailResponse>?}