package com.misit.abpenergy.Apiimport com.misit.abpenergy.Login.Response.CompanyResponseimport com.misit.abpenergy.Login.Response.DaftarAkunResponseimport com.misit.abpenergy.Login.Response.DepartmentResponseimport retrofit2.Callimport retrofit2.http.*interface ApiEndPointTwo {    @GET("/android/api/get/perusahaan")    fun getCompany()            : Call<CompanyResponse>?    @GET("/android/api/get/department")    fun getDepartment(@Query("company") company:String)            : Call<DepartmentResponse>?    @FormUrlEncoded    @POST("/android/api/daftar/akun/baru/mitra")    fun daftarkanAkunMitra(@Field("nik") nik:String?,                            @Field("username") username:String?,                            @Field("password") password:String?,                            @Field("nama") nama:String?,                            @Field("email") email:String?,                            @Field("perusahaan") perusahaan:String?,                            @Field("departemen") departemen:String?,                            @Field("devisi") devisi:String?,                            @Field("_token") csrf_token:String?                        ) : Call<DaftarAkunResponse>?}