package com.misit.abpenergy.Driveimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.Viewimport android.widget.Switchimport com.google.android.gms.auth.api.signin.GoogleSignInimport com.google.android.gms.auth.api.signin.GoogleSignInAccountimport com.google.android.gms.auth.api.signin.GoogleSignInOptionsimport com.google.android.gms.common.api.Scopeimport com.google.android.gms.tasks.OnSuccessListenerimport com.misit.abpenergy.Rimport com.google.api.client.auth.oauth2.Credential;import com.google.api.client.extensions.java6.auth.oauth2.AuthorizationCodeInstalledApp;import com.google.api.client.extensions.jetty.auth.oauth2.LocalServerReceiver;import com.google.api.client.googleapis.auth.oauth2.GoogleAuthorizationCodeFlow;import com.google.api.client.googleapis.auth.oauth2.GoogleClientSecrets;import com.google.api.client.googleapis.auth.oauth2.GoogleCredentialimport com.google.api.client.googleapis.javanet.GoogleNetHttpTransport;import com.google.api.client.http.javanet.NetHttpTransport;import com.google.api.client.json.JsonFactory;import com.google.api.client.json.jackson2.JacksonFactory;import com.google.api.client.util.store.FileDataStoreFactory;import com.google.api.services.drive.Drive;import com.google.api.services.drive.DriveScopes;import com.google.api.services.drive.model.File;import com.google.api.services.drive.model.FileList;class DriveActivity : AppCompatActivity() ,View.OnClickListener{    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_drive)        requestSignIn()    }    private fun requestSignIn() {        var googleSigninOption = GoogleSignInOptions.Builder(GoogleSignInOptions.DEFAULT_GAMES_SIGN_IN)            .requestEmail()            .requestScopes(Scope(DriveScopes.DRIVE_FILE))            .build()        var googleSignInClient = GoogleSignIn.getClient(this@DriveActivity,googleSigninOption)        startActivityForResult(googleSignInClient.signInIntent,2601)    }    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {        when (requestCode){            2601 ->if(resultCode == RESULT_OK){                handleSignIn(data!!)            }        }        super.onActivityResult(requestCode, resultCode, data)    }    private fun handleSignIn(data: Intent) {        GoogleSignIn.getSignedInAccountFromIntent(data).addOnSuccessListener{//            var credential = GoogleAccountCridential        }.addOnFailureListener {        }    }    override fun onClick(v: View?) {    }}