package com.misit.abpenergy.HGE.CutiOnlineimport android.content.Intentimport android.os.Bundleimport android.view.Viewimport androidx.appcompat.app.AppCompatActivityimport com.misit.abpenergy.Rimport kotlinx.android.synthetic.main.activity_cuti_online.*class CutiOnlineActivity : AppCompatActivity(),View.OnClickListener {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_cuti_online)        title = "Cuti Karyawan"        cvCutiUser.setOnClickListener(this@CutiOnlineActivity)    }    override fun onClick(v: View?) {        if(v?.id==R.id.cvCutiUser){            val intent = Intent(this@CutiOnlineActivity,DetailCutiActivity::class.java)            startActivity(intent)        }    }}