package com.misit.abpenergy.HGE.CutiOnlineimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.misit.abpenergy.Rimport kotlinx.android.synthetic.main.activity_cuti_online_all.*class CutiOnlineAllActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_cuti_online_all)        title = "Semua Pengajuan Cuti Karyawan"        cvCutiUser.setOnClickListener {            val i = Intent(this,DetailCutiActivity::class.java)            startActivity(i)        }    }}