package com.misit.abpenergy.HSE.Buletin.Adapterimport android.content.Contextimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport android.widget.TextViewimport androidx.cardview.widget.CardViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Main.Response.MessageInfoItemimport com.misit.abpenergy.Rimport org.joda.time.LocalDateimport org.joda.time.format.DateTimeFormatimport org.joda.time.format.DateTimeFormatterclass BuletinAdapter(private var context: Context,private var listInfo:MutableList<MessageInfoItem>):RecyclerView.Adapter<BuletinAdapter.MyViewHolder>(){    private var onItemClickListener: OnItemClickListener? = null    private val layoutInflater: LayoutInflater    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        val view = layoutInflater.inflate(R.layout.list_buletin,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var buletinList = listInfo[position]        val fmt: DateTimeFormatter = DateTimeFormat.forPattern("d MMMM, yyyy")        holder.tvJudulBuletin.text = buletinList.judul        holder.tvPesanBuletin.text = buletinList.pesan        holder.tvTglBuletin.text = LocalDate.parse(buletinList.tgl).toString(fmt)        holder.btnListUbah.setOnClickListener {            onItemClickListener?.ubah(buletinList)        }        holder.btnListDelete.setOnClickListener {            onItemClickListener?.hapus(buletinList)        }        holder.btnListSH.setOnClickListener {            onItemClickListener?.showHide(buletinList)        }        if(buletinList.status==0){            holder.btnListSH.setText("Sembunyikan")        }else if(buletinList.status==1){            holder.btnListSH.setText("Tampilkan")        }    }    override fun getItemCount(): Int {        return listInfo.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvJudulBuletin = itemView.findViewById<View>(R.id.tvJudulBuletin) as TextView        var tvPesanBuletin = itemView.findViewById<View>(R.id.tvPesanBuletin) as TextView        var tvTglBuletin = itemView.findViewById<View>(R.id.tvTglBuletin) as TextView        var btnListUbah = itemView.findViewById<View>(R.id.btnListUbah) as Button        var btnListDelete = itemView.findViewById<View>(R.id.btnListDelete) as Button        var btnListSH = itemView.findViewById<View>(R.id.btnListSH) as Button    }    interface OnItemClickListener{        fun ubah(buletin:MessageInfoItem)        fun hapus(buletin:MessageInfoItem)        fun showHide(buletin:MessageInfoItem)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(context)    }}