package com.misit.abpenergy.HSE.HazardReport.Activityimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.bumptech.glide.Glideimport com.misit.abpenergy.Rimport kotlinx.android.synthetic.main.activity_image_hazard.*class ImageHazardActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_image_hazard)        var imgHazard = intent.getStringExtra("ImageHazard")        var dir = intent.getStringExtra("Direktori")        var actionBar = supportActionBar        actionBar?.setDisplayHomeAsUpEnabled(true)        Glide.with(this@ImageHazardActivity)            .load(dir+imgHazard)            .into(imgHazardView)    }}