package com.misit.abpenergy.HSE.HazardReport.Adapterimport android.content.Contextimport android.graphics.Colorimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport android.widget.TextViewimport androidx.cardview.widget.CardViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.HSE.HazardReport.Response.KemungkinanItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass KemungkinanAdapter(private val c: Context?,                         private var kemungkinanDipilih: String?,                         private val kemungkinanList:MutableList<KemungkinanItem>):    RecyclerView.Adapter<KemungkinanAdapter.MyViewHolder>() {    private var onItemClickListener: OnItemClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.kemungkinan,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var kemungkinanList =kemungkinanList[position]        holder.tvValue.text = kemungkinanList.kemungkinan        holder.tvNilai.text = kemungkinanList.nilai.toString()        holder.tvKet.text = kemungkinanList.keterangan        Log.d("PESAN","${kemungkinanList.keterangan}");        if(kemungkinanDipilih==kemungkinanList.kemungkinan){            holder.tvValue.setTextColor(Color.parseColor("#FFFFFF"))            holder.tvNilai.setTextColor(Color.parseColor("#FFFFFF"))            holder.tvTXTnilai.setTextColor(Color.parseColor("#FFFFFF"))            holder.tvKet.setTextColor(Color.parseColor("#FFFFFF"))            holder.tvStrKet.setTextColor(Color.parseColor("#FFFFFF"))            holder.lnPilih.setBackgroundColor(Color.parseColor("#4F8E50"))        }else{            holder.tvValue.setTextColor(Color.parseColor("#4F8E50"))            holder.tvNilai.setTextColor(Color.parseColor("#4F8E50"))            holder.tvTXTnilai.setTextColor(Color.parseColor("#4F8E50"))            holder.tvKet.setTextColor(Color.parseColor("#4F8E50"))            holder.tvStrKet.setTextColor(Color.parseColor("#4F8E50"))            holder.lnPilih.setBackgroundColor(Color.parseColor("#FFFFFF"))        }        holder.btnKemungkinan.setOnClickListener{            onItemClickListener?.onItemClick(kemungkinanList.idKemungkinan.toString(),kemungkinanList.kemungkinan!!,kemungkinanList.nilai)        }    }    override fun getItemCount(): Int {        return kemungkinanList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var lnPilih = itemView.findViewById<View>(R.id.lnPilihCustom) as CardView        var tvNilai = itemView.findViewById<View>(R.id.tvNilai) as TextView        var tvTXTnilai = itemView.findViewById<View>(R.id.tvTXTnilai) as TextView        var tvValue = itemView.findViewById<View>(R.id.tvValue) as TextView        var tvKet = itemView.findViewById<View>(R.id.tvKet) as TextView        var tvStrKet = itemView.findViewById<View>(R.id.tvStrKet) as TextView        var btnKemungkinan = itemView.findViewById<View>(R.id.btnKemungkinan) as Button    }    interface OnItemClickListener{        fun onItemClick(uid:String,kemungkinan:String,nilai:Int?)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}