package com.misit.abpenergy.HSE.HazardReport.Responseimport androidx.annotation.Keepimport com.google.gson.annotations.SerializedName@Keepdata class DetKeparahanResponse(	@field:SerializedName("det_keparahan")	val detKeparahan: List<DetKeparahanItem?>? = null)@Keepdata class DetKeparahanItem(	@field:SerializedName("keterangan")	val keterangan: String? = null,	@field:SerializedName("idKeparahan")	val idKeparahan: Int? = null,	@field:SerializedName("id_det")	val idDet: Int? = null,	@field:SerializedName("time_input")	val timeInput: String? = null,	@field:SerializedName("ket_input")	val ketInput: String? = null)