package com.misit.abpenergy.HSE.HazardReport.Serviceimport android.app.*import android.content.Intentimport android.os.Buildimport android.os.IBinderimport android.util.Logimport com.misit.abpenergy.Main.HomePage.IndexActivityimport com.misit.abpenergy.Rimport com.misit.abpenergy.Utils.Constantsclass FgHazardService:Service() {    var TAG ="FgHazardService"    lateinit var manager:NotificationManager    lateinit var hazardSave :HazardSaveOffline    override fun onCreate() {        Log.d(TAG,"onCreate")        hazardSave = HazardSaveOffline()        createNotificationChannel()        super.onCreate()    }    override fun onBind(intent: Intent?): IBinder? {        return null    }    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {        Log.d(TAG,"onStartCommand")        intent.let {            when(it!!.action){                Constants.SERVICE_START -> showNotification()                Constants.SERVICE_STOP -> stopService()            }        }        return super.onStartCommand(intent, flags, startId)    }    private fun showNotification() {        Log.d(TAG,"showNotification")        val notificationIntent = Intent(this, IndexActivity::class.java)        val pendingIntent = PendingIntent.getActivity(this, 1, notificationIntent, 0)        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {            val notification =                Notification.Builder(this, Constants.CHANNEL_ID).setContentText("Synchronize . . .!!!").setSmallIcon(                    R.drawable.abp_white                ).setContentIntent(pendingIntent).build()//                hazardSave.getToken(this@FgHazardService,"SavingHazard","FgHazardDone")                startForeground(Constants.NOTIFICATION_ID, notification)        }    }    private fun createNotificationChannel(){        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O){            val serviceChannel = NotificationChannel(                Constants.CHANNEL_ID,                "Saving Service Channel",                NotificationManager.IMPORTANCE_DEFAULT            )            manager = getSystemService(                NotificationManager::class.java            )            manager.createNotificationChannel(serviceChannel)        }    }    private fun stopService(){        manager.cancel(Constants.NOTIFICATION_ID)        stopForeground(true)        stopSelf()        Log.d(TAG, "Service Stopped!!")    }}