package com.misit.abpenergy.HSE.HazardReport.ViewModelimport android.content.Contextimport android.database.sqlite.SQLiteExceptionimport android.util.Logimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.misit.abpenergy.HSE.HazardReport.Response.ItemHazardListimport com.misit.abpenergy.HSE.HazardReport.SQLite.DataSource.HazardDetailDataSourceclass UnsavedViewModel: ViewModel() {    var listHazard : MutableLiveData<ItemHazardList>    init {        listHazard = MutableLiveData()    }    fun hazardUnsave():MutableLiveData<ItemHazardList>{        return listHazard;    }    fun loadUnsaved(c:Context){        var unsaved = HazardDetailDataSource(c)        try {            var load = unsaved.getFull()            Log.d("sqlError","${load}")            listHazard.postValue(load)        }catch (e:SQLiteException){            Log.d("sqlError","${e.message}")        }    }}