package com.misit.abpenergy.HSE.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.HSE.Inspeksi.Response.InspeksiPicaItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass PicaAdapter(private var c: Context?,                  private val picaList:MutableList<InspeksiPicaItem>):    RecyclerView.Adapter<PicaAdapter.MyViewHolder>() {    private var onItemsClickListener: OnItemsClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view: View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.sumber_bahaya_list,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listPica = picaList[position]        holder.temuan.setText(listPica.temuan)    }    override fun getItemCount(): Int {       return  picaList.size    }    interface OnItemsClickListener{        fun onItemClick(nikTeam:String)    }    fun setListener (listeners: OnItemsClickListener){        onItemsClickListener = listeners    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var temuan = itemView.findViewById<View>(R.id.rbSumberBahaya) as Button    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}