package com.misit.abpenergy.HazardReport.Adapterimport android.content.Contextimport android.graphics.Colorimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.HazardReport.Response.DetHirarkiItemimport com.misit.abpenergy.Rclass DetHirarkiAdapter(    c: Context?,    val detLirarkiList:MutableList<DetHirarkiItem>?,var idDipilih:Int?):    RecyclerView.Adapter<DetHirarkiAdapter.MyViewHolder>() {    private val layoutInflater: LayoutInflater    lateinit var view: View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.detail_pengendalian,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var hirarkiList = detLirarkiList?.get(position)//        Log.d("hirarkiList","${hirarkiList}")        if(idDipilih!=null){            if(idDipilih == hirarkiList?.idHirarki){                holder.detHirarki.setTextColor(Color.parseColor("#FFFFFF"))                holder.textDet.setTextColor(Color.parseColor("#FFFFFF"))            }else{                holder.detHirarki.setTextColor(Color.parseColor("#000000"))                holder.textDet.setTextColor(Color.parseColor("#000000"))            }        }        holder.detHirarki.text = hirarkiList?.keterangan?.capitalize()    }    override fun getItemCount(): Int {        if (detLirarkiList != null) {            return detLirarkiList.size        }else{            return 0        }    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var textDet = itemView.findViewById<View>(R.id.textDet) as TextView        var detHirarki = itemView.findViewById<View>(R.id.detHirarki) as TextView    }    init {        layoutInflater = LayoutInflater.from(c)    }}