package com.misit.abpenergy.HazardReport.Adapterimport android.content.Contextimport android.graphics.Colorimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport android.widget.LinearLayoutimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.HazardReport.Response.HirarkiItemimport com.misit.abpenergy.Rimport org.joda.time.format.DateTimeFormatimport org.joda.time.format.DateTimeFormatterimport java.text.SimpleDateFormatclass HirarkiAdapter(private val c: Context?,                     private var hirarkiDipilih: String?,                     private val hirarkiList:MutableList<HirarkiItem>):    RecyclerView.Adapter<HirarkiAdapter.MyViewHolder>() {    private var onItemClickListener: OnItemClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.sumber_bahaya_list,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var hirarkiList =hirarkiList[position]        val fmt: DateTimeFormatter = DateTimeFormat.forPattern("d MMMM, yyyy")        holder.rbSumberBahaya.text = hirarkiList.namaPengendalian        if(hirarkiDipilih==hirarkiList.namaPengendalian){            holder.rbSumberBahaya.setTextColor(Color.parseColor("#FFFFFF"))            holder.rbSumberBahaya.setBackgroundColor(Color.parseColor("#4F8E50"))            holder.lnPilih.setBackgroundColor(Color.parseColor("#4F8E50"))        }else{            holder.rbSumberBahaya.setTextColor(Color.parseColor("#4F8E50"))            holder.rbSumberBahaya.setBackgroundColor(Color.parseColor("#FFFFFF"))            holder.lnPilih.setBackgroundColor(Color.parseColor("#FFFFFF"))        }        holder.rbSumberBahaya.setOnClickListener{            onItemClickListener?.onItemClick(hirarkiList.idHirarki.toString(),hirarkiList.namaPengendalian!!)        }    }    override fun getItemCount(): Int {        return hirarkiList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var lnPilih = itemView.findViewById<View>(R.id.lnPilih) as LinearLayout        var rbSumberBahaya = itemView.findViewById<View>(R.id.rbSumberBahaya) as Button    }    interface OnItemClickListener{        fun onItemClick(uid:String,pengendalian:String)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}