package com.misit.abpenergy.HazardReport.Adapterimport android.content.Contextimport android.graphics.Colorimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport android.widget.LinearLayoutimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.HazardReport.Response.HirarkiItemimport com.misit.abpenergy.HazardReport.Response.KemungkinanItemimport com.misit.abpenergy.Rimport org.joda.time.format.DateTimeFormatimport org.joda.time.format.DateTimeFormatterimport java.text.SimpleDateFormatclass KemungkinanAdapter(private val c: Context?,                         private var kemungkinanDipilih: String?,                         private val kemungkinanList:MutableList<KemungkinanItem>):    RecyclerView.Adapter<KemungkinanAdapter.MyViewHolder>() {    private var onItemClickListener: OnItemClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.custom_select,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var kemungkinanList =kemungkinanList[position]        val fmt: DateTimeFormatter = DateTimeFormat.forPattern("d MMMM, yyyy")        holder.tvValue.text = kemungkinanList.kemungkinan        holder.tvNilai.text = kemungkinanList.nilai.toString()        if(kemungkinanDipilih==kemungkinanList.kemungkinan){            holder.tvValue.setTextColor(Color.parseColor("#FFFFFF"))            holder.tvNilai.setTextColor(Color.parseColor("#FFFFFF"))            holder.tvTXTnilai.setTextColor(Color.parseColor("#FFFFFF"))            holder.lnPilih.setBackgroundColor(Color.parseColor("#4F8E50"))        }else{            holder.tvValue.setTextColor(Color.parseColor("#4F8E50"))            holder.tvNilai.setTextColor(Color.parseColor("#4F8E50"))            holder.tvTXTnilai.setTextColor(Color.parseColor("#4F8E50"))            holder.lnPilih.setBackgroundColor(Color.parseColor("#FFFFFF"))        }        holder.lnPilih.setOnClickListener{            onItemClickListener?.onItemClick(kemungkinanList.idKemungkinan.toString(),kemungkinanList.kemungkinan!!)        }    }    override fun getItemCount(): Int {        return kemungkinanList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var lnPilih = itemView.findViewById<View>(R.id.lnPilihCustom) as LinearLayout        var tvNilai = itemView.findViewById<View>(R.id.tvNilai) as TextView        var tvTXTnilai = itemView.findViewById<View>(R.id.tvTXTnilai) as TextView        var tvValue = itemView.findViewById<View>(R.id.tvValue) as TextView    }    interface OnItemClickListener{        fun onItemClick(uid:String,kemungkinan:String)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}