package com.misit.abpenergy.HazardReportimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.bumptech.glide.Glideimport com.bumptech.glide.load.engine.DiskCacheStrategyimport com.misit.abpenergy.Rimport kotlinx.android.synthetic.main.activity_detail_hazard.*import kotlinx.android.synthetic.main.activity_image_hazard.*class ImageHazardActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_image_hazard)        var imgHazard = intent.getStringExtra("ImageHazard")        var dir = intent.getStringExtra("Direktori")        Glide.with(this@ImageHazardActivity)            .load(dir+imgHazard)            .diskCacheStrategy(DiskCacheStrategy.NONE)            .into(imgHazardView)    }}