package com.misit.abpenergy.HazardReport.SQLite.DataSourceimport android.content.ContentValuesimport android.content.Contextimport android.database.Cursorimport android.database.sqlite.SQLiteDatabaseimport com.misit.abpenergy.HazardReport.Response.HazardItemimport com.misit.abpenergy.HazardReport.SQLite.Model.HazardHeaderModelimport com.misit.abpenergy.HazardReport.SQLite.Model.MetrikModelimport com.misit.abpenergy.SQLite.DbHelperimport kotlinx.coroutines.asyncimport kotlinx.coroutines.coroutineScopeclass MetrikDataSource(val c: Context) {    var dbHelper : DbHelper    var sqlDatabase : SQLiteDatabase?=null    var listItem :ArrayList<MetrikModel>?=null    init {        listItem = ArrayList()        dbHelper = DbHelper(c)    }    companion object{        val tbItem = "metrik_resiko"    }    private fun openAccess(){        sqlDatabase = dbHelper.writableDatabase    }    private fun closeAccess(){        sqlDatabase?.close()        dbHelper?.close()    }    fun insertItem(item: MetrikModel):Long{        openAccess()        var cv = createCV(item)        var hasil = sqlDatabase?.insertOrThrow("${tbItem}",null,cv)        closeAccess()        return hasil!!    }    suspend fun getMetrik(nilai:Int): MetrikModel {        var listItem = MetrikModel()        coroutineScope {            val c = sqlDatabase?.rawQuery("SELET * FROM " +                    "$tbItem WHERE " +                    "max >='"+nilai+"' " +                    "AND " +                    "min <='"+nilai+"' " ,                    null)            val first = async { c?.moveToFirst() }            if(first.await()==true){                val sendItem = async { fetchRow(c!!) }                if(sendItem.await()!=null){                    listItem = sendItem.await()                    c?.close()                    closeAccess()                }            }        }        return listItem    }    private fun fetchRow(cursor: Cursor): MetrikModel {        val idResiko = cursor.getInt(cursor.getColumnIndex("idResiko"))        val min = cursor.getInt(cursor.getColumnIndex("min"))        val max = cursor.getInt(cursor.getColumnIndex("max"))        val kategori = cursor.getString(cursor.getColumnIndex("kategori"))        val tindakan = cursor.getString(cursor.getColumnIndex("tindakan"))        val bgColor = cursor.getString(cursor.getColumnIndex("bgColor"))        val txtColor = cursor.getString(cursor.getColumnIndex("txtColor"))        val flag = cursor.getInt(cursor.getColumnIndex("flag"))        val metrikModel = MetrikModel()        metrikModel.idResiko = idResiko        metrikModel.min = min        metrikModel.max = max        metrikModel.kategori = kategori        metrikModel.tindakan = tindakan        metrikModel.bgColor = bgColor        metrikModel.flag = flag        metrikModel.txtColor = txtColor        return metrikModel    }    private fun createCV(item : MetrikModel): ContentValues {        var cv = ContentValues()        cv.put("idResiko",item.idResiko)        cv.put("min",item.min)        cv.put("max",item.max)        cv.put("kategori",item.kategori)        cv.put("tindakan",item.tindakan)        cv.put("bgColor",item.bgColor)        cv.put("flag",item.flag )        cv.put("txtColor",item.txtColor)        return cv    }    fun deleteAll(): Boolean {        openAccess()        val hasil = sqlDatabase?.delete("${tbItem}",null,null)        if(hasil!! <0 ){            return false        }        closeAccess()        return true    }}