package com.misit.abpenergy.HazardReport.Serviceimport android.app.*import android.content.Intentimport android.content.res.Resourcesimport com.misit.abpenergy.Rclass BgHazardService :IntentService(Resources.getSystem().getString(R.string.pref_key)){    lateinit var manager:NotificationManager    lateinit var hazardSave :HazardSaveOffline    override fun onCreate() {        hazardSave = HazardSaveOffline()        super.onCreate()    }    override fun onHandleIntent(intent: Intent?) {        hazardSave.getToken(this@BgHazardService,"SavingHazard","BgHazardDone")    }}