package com.misit.abpenergyimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.Menuimport android.view.MenuItemimport android.widget.Toastimport es.dmoral.toasty.Toastyimport kotlinx.android.synthetic.main.activity_home.*class HomeActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_home)        bottomAppBar.replaceMenu(R.menu.bottom_app_bar)        fabIC.setOnClickListener {            Toasty.info(this,"FAB CLICKED").show()        }        bottomAppBar.setOnMenuItemClickListener {item->            when(item.itemId){                R.id.newForm1->{                    Toasty.info(this@HomeActivity,"A").show()                }            }            true        }    }    override fun onOptionsItemSelected(item: MenuItem): Boolean {        when(item.itemId){            R.id.newForm1->{                Toasty.info(this@HomeActivity,"A").show()            }        }        return super.onOptionsItemSelected(item)    }}