package com.misit.abpenergy.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Inspeksi.Response.InpeksiItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass ALLInspectionAdapter(private var c: Context?,                           private val inspeksiList:MutableList<InpeksiItem>):    RecyclerView.Adapter<ALLInspectionAdapter.MyViewHolder>() {    private var onItemsClickListener: OnItemsClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.inspeksi_list,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        val listInpeksi = inspeksiList[position]        holder.tvNamaInspeksi.text = listInpeksi.namaForm    }    override fun getItemCount(): Int {        return inspeksiList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvNamaInspeksi = itemView.findViewById<View>(R.id.tvNamaInspeksi) as TextView    }    interface OnItemsClickListener{        fun onItemClick(idItem:String)    }    fun setListener (listeners: OnItemsClickListener){        onItemsClickListener = listeners    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}