package com.misit.abpenergy.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.RadioButtonimport android.widget.RadioGroupimport android.widget.TextViewimport androidx.cardview.widget.CardViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Inspeksi.Response.DataItemInspeksiimport com.misit.abpenergy.Rimport es.dmoral.toasty.Toastyimport java.text.SimpleDateFormatclass InspeksiDataAdapater(private var c: Context?,                           private val headerList:MutableList<DataItemInspeksi>):    RecyclerView.Adapter<InspeksiDataAdapater.MyViewHolder>() {    private var onItemsClickListener: OnItemsClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.inspeksi_list,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        val listHeader = headerList[position]        holder.tvNamaInspeksi.text = listHeader.namaForm        holder.tvTglInspeksi.text = listHeader.tglInspeksi        holder.tvPerusahaanPerusahaan.text = listHeader.namaPerusahaan        holder.tvLokasiInspeksi.text = listHeader.lokasiInspeksi        holder.tvSaranInspeksi.text = listHeader.saran        holder.tvStatus.text = listHeader.status        if(listHeader.flag!! < 0){            holder.tvValidasi.text = "Belum DiSetujui Oleh Safety"        }else{            holder.tvValidasi.text = "DiSetujui Oleh Safety"        }        holder.cvInspeksiHeader.setOnClickListener {            Toasty.info(c!!,"${listHeader.idInspeksi}").show()        }    }    override fun getItemCount(): Int {        return headerList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvTglInspeksi = itemView.findViewById<View>(R.id.tvTglInspeksi) as TextView        var tvNamaInspeksi = itemView.findViewById<View>(R.id.tvNamaInspeksi) as TextView        var tvPerusahaanPerusahaan = itemView.findViewById<View>(R.id.tvPerusahaanPerusahaan) as TextView        var tvLokasiInspeksi = itemView.findViewById<View>(R.id.tvLokasiInspeksi) as TextView        var tvSaranInspeksi = itemView.findViewById<View>(R.id.tvSaranInspeksi) as TextView        var tvStatus = itemView.findViewById<View>(R.id.tvStatus) as TextView        var tvValidasi = itemView.findViewById<View>(R.id.tvValidasi) as TextView        var cvInspeksiHeader = itemView.findViewById<View>(R.id.cvInspeksiHeader) as CardView    }    interface OnItemsClickListener{        fun onItemClick(idItem:String)    }    fun setListener (listeners: OnItemsClickListener){        onItemsClickListener = listeners    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}