package com.misit.abpenergy.Inspeksi.Adapterimport com.google.gson.annotations.SerializedNamedata class InspeksiPicaDetail (    @field:SerializedName("inspeksiPica")    val inspeksiPica: List<ItemPicaDetail>? = null)data class ItemPicaDetail(    @field:SerializedName("idForm")    val idForm: Int? = null,    @field:SerializedName("idPica")    val idPica: Int? = null,    @field:SerializedName("temuan")    val temuan: String? = null,    @field:SerializedName("buktiTemuan")    val buktiTemuan: String? = null,    @field:SerializedName("nikPJ")    val nikPJ: String? = null,    @field:SerializedName("tglTenggat")    val tglTenggat: String? = null,    @field:SerializedName("namaPJ")    val namaPJ: String? = null,    @field:SerializedName("status")    val status: String? = null)