package com.misit.abpenergy.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Inspeksi.Response.ItemsInspeksiModelsimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass ItemsDetailAdapter(private var c: Context?, private var idForm:String, private var uniqueID:String,                         private val itemsList:MutableList<ItemsInspeksiModels>):    RecyclerView.Adapter<ItemsDetailAdapter.MyViewHolder>() {    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view: View    private var yes:ArrayList<String>?=null    private var no:ArrayList<String>?=null    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        yes = ArrayList()        no = ArrayList()        view = layoutInflater.inflate(R.layout.item_sub_detail,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listItems = itemsList[position]        holder.tvNumItem.text = "${position+1}"        holder.tvItems.text = listItems.listInspeksi        if(listItems.answere==0){            holder.itemAnswere.text = "Tidak"        }else if(listItems.answere==1){            holder.itemAnswere.text = "Ya"        }    }    override fun getItemCount(): Int {        return itemsList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvItems = itemView.findViewById<View>(R.id.tvItems) as TextView        var itemAnswere = itemView.findViewById<View>(R.id.itemAnswere) as TextView        var tvNumItem = itemView.findViewById<View>(R.id.tvNumItem) as TextView    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}