package com.misit.abpenergy.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.RadioButtonimport android.widget.RadioGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Inspeksi.Response.ItemsInspeksiModelsimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass ItemsInspeksiAdapter(private var c: Context?,private var idForm:String,private var uniqueID:String,                           private val itemsList:MutableList<ItemsInspeksiModels>):    RecyclerView.Adapter<ItemsInspeksiAdapter.MyViewHolder>() {    private var onItemsClickListener: OnItemsClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.sub_inspeksi_items,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listItems = itemsList[position]//        Toasty.info(c!!,listItems.listInspeksi!!).show()        holder.tvNumItem.text = "${position+1}"        holder.tvItems.text = listItems.listInspeksi        holder.rbYes.setOnClickListener{            onItemsClickListener!!.onRadioClick(idForm,listItems.idList.toString(),uniqueID,1,holder)        }        holder.rbNo.setOnClickListener{            onItemsClickListener!!.onRadioClick(idForm,listItems.idList.toString(),uniqueID,0,holder)        }    }    override fun getItemCount(): Int {        return itemsList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvItems = itemView.findViewById<View>(R.id.tvItems) as TextView        var rbYes = itemView.findViewById<View>(R.id.rbYes) as RadioButton        var rbNo = itemView.findViewById<View>(R.id.rbNo) as RadioButton        var rdItemInspeksi = itemView.findViewById<View>(R.id.rdItemInspeksi) as RadioGroup        var tvNumItem = itemView.findViewById<View>(R.id.tvNumItem) as TextView    }    interface OnItemsClickListener{        fun onRadioClick(idForm:String,idItem:String,uniqueID:String,option:Int,holder:MyViewHolder)    }    fun setListener (listeners: OnItemsClickListener){        onItemsClickListener = listeners    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}