package com.misit.abpenergy.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.RadioButtonimport android.widget.RadioGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Inspeksi.Response.ItemsInspeksiModelsimport com.misit.abpenergy.Inspeksi.Response.ItemsItemimport com.misit.abpenergy.Rimport es.dmoral.toasty.Toastyimport java.text.SimpleDateFormatclass ItemsInspeksiAdapter(private var c: Context?,                           private val itemsList:MutableList<ItemsInspeksiModels>):    RecyclerView.Adapter<ItemsInspeksiAdapter.MyViewHolder>() {    private var onItemsClickListener: OnItemsClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.sub_inspeksi_items,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listItems = itemsList[position]//        Toasty.info(c!!,listItems.listInspeksi!!).show()        holder.tvItems.text = listItems.listInspeksi    }    override fun getItemCount(): Int {        return itemsList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvItems = itemView.findViewById<View>(R.id.tvItems) as TextView        var rbYes = itemView.findViewById<View>(R.id.rbYes) as RadioButton        var rbNo = itemView.findViewById<View>(R.id.rbNo) as RadioButton        var rdItemInspeksi = itemView.findViewById<View>(R.id.rdItemInspeksi) as RadioGroup    }    interface OnItemsClickListener{        fun onItemsClick(idForm:String,nameForm:String)    }    fun setListener (listeners: OnItemsClickListener){        onItemsClickListener = listeners    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}