package com.misit.abpenergy.Inspeksi.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Inspeksi.Response.TeamInspeksiItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass TeamDetailAdapter(private var c: Context?,                        private val teamItems:MutableList<TeamInspeksiItem>):    RecyclerView.Adapter<TeamDetailAdapter.MyViewHolder>() {    private var onItemsClickListener: OnItemsClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view: View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.team_inspeksi,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var itemTeam = teamItems[position]        holder.tvTeamNik.text = itemTeam.nik        holder.tvTeamNama.text = itemTeam.namaLengkap        holder.tvTeamDept.text = itemTeam.dept        holder.tvTeamSection.text = itemTeam.sect    }    override fun getItemCount(): Int {        return teamItems.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvTeamNik = itemView.findViewById<View>(R.id.tvTeamNik) as TextView        var tvTeamNama = itemView.findViewById<View>(R.id.tvTeamNama) as TextView        var tvTeamSection = itemView.findViewById<View>(R.id.tvTeamSection) as TextView        var tvTeamDept = itemView.findViewById<View>(R.id.tvTeamDept) as TextView    }    interface OnItemsClickListener{        fun onItemClick(nikTeam:String)    }    fun setListener (listeners: OnItemsClickListener){        onItemsClickListener = listeners    }    init {        layoutInflater = LayoutInflater.from(c)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}