package com.misit.abpenergy.Inspeksiimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.Viewimport android.view.Windowimport android.view.WindowManagerimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.swiperefreshlayout.widget.SwipeRefreshLayoutimport com.misit.abpenergy.Inspeksi.Adapter.ListFormInspeksiAdapaterimport com.misit.abpenergy.Inspeksi.ListInspeksiActivity.Companion.IDFORMimport com.misit.abpenergy.Inspeksi.Response.FormItemimport com.misit.abpenergy.Login.LoginActivityimport com.misit.abpenergy.Rimport com.misit.abpenergy.Utils.PrefsUtilimport kotlinx.android.synthetic.main.activity_inspeksi.*import kotlinx.android.synthetic.main.activity_list_inspeksi.*class ListInspeksiActivity : AppCompatActivity(),View.OnClickListener {    var idForm:String? = null    var nameForm:String? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_list_inspeksi)        idForm = intent.getStringExtra(IDFORM)        nameForm = intent.getStringExtra(NAMEFORM)        title = nameForm        PrefsUtil.initInstance(this)        val window: Window = this.window        window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS)        window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS)        window.statusBarColor = ContextCompat.getColor(this, R.color.colorPrimary)        var actionBar = supportActionBar        actionBar?.setDisplayHomeAsUpEnabled(true)        if(PrefsUtil.getInstance().getBooleanState("IS_LOGGED_IN",false)){            USERNAME = PrefsUtil.getInstance().getStringState(PrefsUtil.USER_NAME,"")        }else{            val intent = Intent(this, LoginActivity::class.java)            startActivity(intent)            finish()        }        floatingNewInspeksi.setOnClickListener(this)    }    override fun onClick(v: View?) {        if(v?.id==R.id.floatingNewInspeksi){            var intent = Intent(this@ListInspeksiActivity,NewInspeksiActivity::class.java)            intent.putExtra(NewInspeksiActivity.IDFORM,idForm)            intent.putExtra(NAMEFORM,nameForm)            startActivityForResult(intent,100)        }    }    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {        super.onActivityResult(requestCode, resultCode, data)    }    override fun onSupportNavigateUp(): Boolean {        onBackPressed()        return super.onSupportNavigateUp()    }    companion object{        var USERNAME = "USERNAME"        var IDFORM = "IDFORM"        var NAMEFORM = "NAMEFORM"    }}