package com.misit.abpenergy.Login.Adapterimport android.content.Contextimport android.graphics.Colorimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Login.Response.CompanyItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass CompanyAdapater(private val context: Context?,                      private var companyDipilih:String,                      private val companyList:MutableList<CompanyItem>):    RecyclerView.Adapter<CompanyAdapater.MyViewHolder>() {    private var onItemClickListener: OnItemClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.sumber_bahaya_list,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listCompany = companyList[position]        holder.perusahaan.setText(listCompany.namaPerusahaan)        if(companyDipilih==listCompany.namaPerusahaan){            holder.perusahaan.setTextColor(Color.parseColor("#FFFFFF"))            holder.perusahaan.setBackgroundColor(Color.parseColor("#4F8E50"))        }else{            holder.perusahaan.setTextColor(Color.parseColor("#4F8E50"))            holder.perusahaan.setBackgroundColor(Color.parseColor("#FFFFFF"))        }        holder.perusahaan.setOnClickListener {            onItemClickListener?.onItemClick(listCompany.idPerusahaan.toString(),listCompany.namaPerusahaan)        }    }    override fun getItemCount(): Int {        return companyList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var perusahaan = itemView.findViewById<View>(R.id.rbSumberBahaya) as Button    }    interface OnItemClickListener{        fun onItemClick(idCompany:String?,namaPerusahaan:String?)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(context)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}