package com.misit.abpenergy.Login.Adapterimport android.content.Contextimport android.graphics.Colorimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Login.Response.DepartmentItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass DepartmentAdapater(private val context: Context?,                         private var departmentDipilih:String?,                         private val deptList:MutableList<DepartmentItem>):    RecyclerView.Adapter<DepartmentAdapater.MyViewHolder>() {    private var onItemClickListener: OnItemClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.sumber_bahaya_list,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listDept = deptList[position]        holder.department.setText(listDept.dept)        if(departmentDipilih==listDept.dept){            holder.department.setTextColor(Color.parseColor("#FFFFFF"))            holder.department.setBackgroundColor(Color.parseColor("#4F8E50"))        }else{            holder.department.setTextColor(Color.parseColor("#4F8E50"))            holder.department.setBackgroundColor(Color.parseColor("#FFFFFF"))        }        holder.department.setOnClickListener {            onItemClickListener?.onItemClick(listDept.idDept.toString(),listDept.dept)        }    }    override fun getItemCount(): Int {        return deptList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var department = itemView.findViewById<View>(R.id.rbSumberBahaya) as Button    }    interface OnItemClickListener{        fun onItemClick(idDept:String?,dept:String?)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(context)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}