package com.misit.abpenergy.Login.Adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.CheckBoximport android.widget.LinearLayoutimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Login.Response.SectionItemimport com.misit.abpenergy.Rimport java.text.SimpleDateFormatclass SectionListAdapter(private val context: Context?,                         private val sectionList:MutableList<SectionItem>):    RecyclerView.Adapter<SectionListAdapter.MyViewHolder>() {    private var onItemClickListener: SectionListAdapter.OnItemClickListener? = null    private val layoutInflater: LayoutInflater    private var simpleDateFormat: SimpleDateFormat? = null    lateinit var view:View    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        view = layoutInflater.inflate(R.layout.list_devisi,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        var listSection =sectionList[position]        holder.departemen.text=listSection.dept        holder.devisi.text=listSection.sect        holder.lnSection.setOnClickListener {            onItemClickListener?.onItemClick(listSection.idSect,listSection.sect)        }    }    override fun getItemCount(): Int {       return sectionList.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var departemen = itemView.findViewById<View>(R.id.tvDept) as TextView        var devisi = itemView.findViewById<View>(R.id.tvSection) as TextView        var cbSection = itemView.findViewById<View>(R.id.cbSection) as CheckBox        var lnSection = itemView.findViewById<View>(R.id.lnSection) as LinearLayout    }    interface OnItemClickListener{        fun onItemClick(idSect:String?,devisi:String?)    }    fun setListener (listener: OnItemClickListener){        onItemClickListener = listener    }    init {        layoutInflater = LayoutInflater.from(context)        simpleDateFormat= SimpleDateFormat("yyyy-MM-dd")    }}