package com.misit.abpenergy.Loginimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.misit.abpenergy.Rimport es.dmoral.toasty.Toastyclass SectionActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_section)        var idDept = intent.getStringExtra(ID_DEPARTEMEN)        Toasty.info(this,idDept.toString()).show()    }    companion object{        var ID_DEPARTEMEN = "ID_DEPARTEMEN"    }}