package com.misit.abpenergy.SQLiteimport android.content.Contextimport android.database.sqlite.SQLiteDatabaseimport android.database.sqlite.SQLiteOpenHelperclass DbHelper(c:Context):SQLiteOpenHelper(c,DB_NAME,null,1) {    companion object{        val DB_NAME = "abp.db"    }    override fun onCreate(db: SQLiteDatabase?) {        db?.execSQL("CREATE TABLE INSPEKSI_ITEM_COUNTER "+                "(ID INTEGER PRIMARY KEY AUTOINCREMENT, "+                "UNIQUEID TEXT,YES INTEGER,NO INTEGER,TOTAL INTEGER)")        db?.execSQL("CREATE TABLE PENUMPANG "+                "(id INTEGER PRIMARY KEY,"+                " nik TEXT ,"+                " nama TEXT ,"+                " jabatan TEXT)")    }    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {        db?.execSQL("DROP TABLE IF EXISTS INSPEKSI_ITEM_COUNTER")        db?.execSQL("DROP TABLE IF EXISTS PENUMPANG")        onCreate(db)    }}