package com.misit.abpenergy.SQLiteimport android.content.Contextimport android.database.sqlite.SQLiteDatabaseimport android.database.sqlite.SQLiteOpenHelperclass DbHelper(c:Context):SQLiteOpenHelper(c,DB_NAME,null,5) {    companion object{        val DB_NAME = "abp.db"        val tb = arrayOf("INSPEKSI_ITEM_COUNTER","PENUMPANG","KEMUNGKINAN","KEPARAHAN","PERUSAHAAN","LOKASI","PENGENDALIAN")    }    override fun onCreate(db: SQLiteDatabase?) {        db?.execSQL("CREATE TABLE INSPEKSI_ITEM_COUNTER "+                "(ID INTEGER PRIMARY KEY AUTOINCREMENT, "+                "UNIQUEID TEXT,YES INTEGER,NO INTEGER,TOTAL INTEGER)")        db?.execSQL("CREATE TABLE PENUMPANG "+                "(id INTEGER PRIMARY KEY,"+                " nik TEXT ,"+                " nama TEXT ,"+                " jabatan TEXT)")        db?.execSQL("CREATE TABLE KEMUNGKINAN "+                "(idKemungkinan INTEGER PRIMARY KEY,"+                " kemungkinan TEXT ,"+                " flag INTEGER ,"+                " nilai INTEGER)")        db?.execSQL("CREATE TABLE KEPARAHAN "+                "(idKeparahan INTEGER PRIMARY KEY,"+                " keparahan TEXT ,"+                " flag INTEGER ,"+                " nilai INTEGER)")        db?.execSQL("CREATE TABLE PERUSAHAAN "+                "(idPerusahaan INTEGER PRIMARY KEY,"+                " namaPerusahaan TEXT ,"+                " flag INTEGER ,"+                " timeIn TEXT)")        db?.execSQL("CREATE TABLE LOKASI "+                "(idLok INTEGER PRIMARY KEY,"+                " userInput TEXT ,"+                " lokasi TEXT ,"+                " tglInput TEXT)")        db?.execSQL("CREATE TABLE PENGENDALIAN "+                "(idRisk INTEGER PRIMARY KEY,"+                " risk TEXT ,"+                " txtColor TEXT ,"+                " userInput TEXT ,"+                " bgColor TEXT ,"+                " descRisk TEXT ,"+                " tglInput TEXT)")        db?.execSQL("CREATE TABLE PENGENDALIAN "+                "(idRisk INTEGER PRIMARY KEY,"+                " risk TEXT ,"+                " txtColor TEXT ,"+                " userInput TEXT ,"+                " bgColor TEXT ,"+                " descRisk TEXT ,"+                " tglInput TEXT)")    }    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {        tb.forEach {            db?.execSQL("DROP TABLE IF EXISTS ${it}")        }        onCreate(db)    }}