package com.misit.abpenergy.SQLiteimport android.database.sqlite.SQLiteDatabaseobject dbQuery {    fun tbItemInspeksi(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE INSPEKSI_ITEM_COUNTER "+                "(ID INTEGER PRIMARY KEY AUTOINCREMENT, "+                "UNIQUEID TEXT,YES INTEGER,NO INTEGER,TOTAL INTEGER)")    }    fun tbpenumpang(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE PENUMPANG "+                "(id INTEGER PRIMARY KEY,"+                " nik TEXT ,"+                " nama TEXT ,"+                " jabatan TEXT)")    }    fun tbKemungkinan(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE KEMUNGKINAN "+                "(idKemungkinan INTEGER PRIMARY KEY,"+                " kemungkinan TEXT ,"+                " flag INTEGER ,"+                " nilai INTEGER)")    }    fun tbKkeparahan(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE KEPARAHAN "+                "(idKeparahan INTEGER PRIMARY KEY,"+                " keparahan TEXT ,"+                " flag INTEGER ,"+                " nilai INTEGER)")    }    fun tbPerusahaan(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE PERUSAHAAN "+                "(idPerusahaan INTEGER PRIMARY KEY,"+                " namaPerusahaan TEXT ,"+                " flag INTEGER ,"+                " timeIn TEXT)")    }    fun tbLokasi(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE LOKASI "+                "(idLok INTEGER PRIMARY KEY,"+                " userInput TEXT ,"+                " lokasi TEXT ,"+                " tglInput TEXT)")    }    fun tbPengendalian(db:SQLiteDatabase?) {        db?.execSQL(            "CREATE TABLE PENGENDALIAN " +                    "(idRisk INTEGER PRIMARY KEY," +                    " risk TEXT ," +                    " txtColor TEXT ," +                    " userInput TEXT ," +                    " bgColor TEXT ," +                    " descRisk TEXT ," +                    " tglInput TEXT)"        )    }    fun tbUsers(db:SQLiteDatabase?){        db?.execSQL("CREATE TABLE USERS "+                "(idUser INTEGER PRIMARY KEY,"+                " tglentry TEXT ,"+                " level TEXT ,"+                " ttd TEXT ,"+                " photoProfile TEXT ,"+                " namaLengkap TEXT ,"+                " idSession TEXT ,"+                " rule TEXT ,"+                " perusahaan INTEGER ,"+                " section TEXT ,"+                " namaPerusahaan TEXT ,"+                " dept TEXT ,"+                " nik TEXT ,"+                " password TEXT ,"+                " sect TEXT ,"+                " department TEXT ,"+                " email TEXT ,"+                " username TEXT ,"+                " status INTEGER)")    }}