package com.misit.abpenergy.SQLiteimport android.database.sqlite.SQLiteDatabaseobject dbQueryInspeksi {    fun tbForm(db: SQLiteDatabase?){        db?.execSQL("CREATE TABLE form_inspeksi "+                "(idForm  INTEGER PRIMARY KEY, "+                "kodeForm TEXT," +                "namaForm TEXT," +                "userEntry TEXT," +                "tglEntry TEXT," +                "flag INTEGER)")    }    fun tbList(db: SQLiteDatabase?){        db?.execSQL("CREATE TABLE form_inspeksi_list "+                "(idList INTEGER PRIMARY KEY, "+                "idForm INTEGER," +                "idSub INTEGER," +                "user_input TEXT," +                "tgl_input TEXT," +                "flag INTEGER," +                "listInspeksi TEXT)")    }    fun tbSub(db: SQLiteDatabase?){        db?.execSQL("CREATE TABLE form_inspeksi_sub "+                "(idSub  INTEGER PRIMARY KEY, "+                "idForm INTEGER," +                "numSub TEXT," +                "nameSub TEXT," +                "user_input TEXT," +                "tgl_input TEXT," +                "flag INTEGER)")    }}