package com.misit.abpenergy.Serviceimport android.app.IntentServiceimport android.content.Intentimport android.os.IBinderimport android.util.Logimport com.misit.abpenergy.Utils.ConfigUtilclass ConnectionService:IntentService("ConnectionService"){    val TAG = "SaranaService"    override fun onBind(intent: Intent): IBinder? {        return null    }    override fun onHandleIntent(intent: Intent?) {        Log.d(TAG,"On Intent")        if(ConfigUtil.cekKoneksi(this@ConnectionService)){            Log.d(TAG,"Connected!")        }else{            Log.d(TAG,"Not Connected!")        }    }    override fun onCreate() {        showLog("onCreate")        super.onCreate()    }    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {        showLog("onStartCommand")        return super.onStartCommand(intent, flags, startId)    }    override fun onDestroy() {        showLog("onDestroy")        super.onDestroy()    }    private fun showLog(s: String) {        Log.d(TAG,s)    }}