package com.misit.abpenergy.Serviceimport android.app.PendingIntentimport android.content.Contextimport android.content.Intentimport android.util.Logimport androidx.work.Workerimport androidx.work.WorkerParametersimport com.misit.abpenergy.HomePage.IndexActivityimport com.misit.abpenergy.Utils.ConfigUtilclass HazardWorking(context: Context, workerParams: WorkerParameters) :Worker(context, workerParams) {    var c = context    override fun doWork(): Result {        var z =101        while (z<=1000){            if(z==1000){                processWork(c,"$z")                Log.d("WorkerManager","Success ${z} | ${c}")                return Result.success()            }            z++        }        return Result.success()    }    private fun processWork(c:Context,counter:String){        val intent = Intent(applicationContext,IndexActivity::class.java).apply {            flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK        }        var rCode = (100..1000).random()        ConfigUtil.showNotification(applicationContext,"Work Manager","Notification ${counter}",intent,rCode,"WorkManager")    }}