package com.misit.abpenergy.Serviceimport android.app.*import android.content.Intentimport android.os.Buildimport android.os.IBinderimport android.util.Logimport com.misit.abpenergy.OlderClass.NewIndexActivityimport com.misit.abpenergy.Rimport com.misit.abpenergy.Utils.Constantsimport com.misit.abpenergy.Utils.InitUtilclass InitService:Service() {    lateinit var manager: NotificationManager    lateinit var initUtil : InitUtil    var username:String?=null    override fun onBind(intent: Intent?): IBinder? {        return null    }    override fun onCreate() {        initUtil= InitUtil(this)        createNotificationChannel()        super.onCreate()    }    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {        intent.let {            username = intent?.getStringExtra("username")            when(it!!.action){                Constants.SERVICE_START -> showNotification(username)                Constants.SERVICE_STOP -> stopService()            }        }        return super.onStartCommand(intent, flags, startId)    }    private fun showNotification(username:String?) {        Log.d("LoadingServices","ShowNotification")        val notificationIntent = Intent(this, NewIndexActivity::class.java)        val pendingIntent = PendingIntent.getActivity(this, 1, notificationIntent, 0)        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {            val notification =                Notification.Builder(this, Constants.CHANNEL_ID).setContentText("Mengambil data....!!!").setSmallIcon(                    R.drawable.abp_white                ).setContentIntent(pendingIntent).build()            initUtil.loadUsers("fgUser","fgDone",this,username)            startForeground(Constants.NOTIFICATION_ID, notification)            Log.d("LoadingServices", "Service Started!!")        }    }    private fun createNotificationChannel(){        Log.d("LoadingServices","CreateChannel")        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O){            val serviceChannel = NotificationChannel(                Constants.CHANNEL_ID,                "Saving Service Channel",                NotificationManager.IMPORTANCE_DEFAULT            )            manager = getSystemService(                NotificationManager::class.java            )            manager.createNotificationChannel(serviceChannel)        }    }    private fun stopService(){        manager.cancel(Constants.NOTIFICATION_ID)        stopForeground(true)        stopSelf()        Log.d("LoadingServices", "Service Stopped!!")    }    fun serviceForceStop(){        manager.cancel(Constants.NOTIFICATION_ID)        stopForeground(true)        stopSelf()        Log.d("LoadingServices", "Service Stopped!!")    }}