package com.misit.abpenergy.Serviceimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.Windowimport android.view.WindowManagerimport android.webkit.WebViewimport android.webkit.WebViewClientimport androidx.core.content.ContextCompatimport com.misit.abpenergy.Rimport com.misit.abpenergy.Utils.PrefsUtilclass MatrikResikoWebViewActivity : AppCompatActivity() {    lateinit var webView:WebView    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_matrik_resiko_web_view)        title="Table Matrik Resiko"        var actionBar = supportActionBar        actionBar?.setDisplayHomeAsUpEnabled(true)        val window: Window = this.window        window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS)        window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS)        window.statusBarColor = ContextCompat.getColor(this, R.color.colorPrimary)        webView = findViewById(R.id.webView)        webView.settings.setJavaScriptEnabled(true)        webView.webViewClient = object : WebViewClient() {            override fun shouldOverrideUrlLoading(view: WebView?, url: String?): Boolean {                if (url != null) {                    view?.loadUrl(url)                }                return true            }        }        webView!!.loadUrl("https://abpjobsite.com/hse/admin/matrik/table/web/view")    }    override fun onSupportNavigateUp(): Boolean {        onBackPressed()        return super.onSupportNavigateUp()    }}