package com.misit.abpenergy.Socketimport android.content.Contextimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.misit.abpenergy.Rclass ChatAdapter(private var context: Context,private var listChat:ArrayList<String>):    RecyclerView.Adapter<ChatAdapter.MyViewHolder>() {    private val layoutInflater: LayoutInflater    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        val view = layoutInflater.inflate(R.layout.list_chat,parent,false)        return MyViewHolder(view)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {       var chatList = listChat[position]        Log.d("chatList","$chatList")        holder.tvChat.text = "$chatList"    }    override fun getItemCount(): Int {        return listChat.size    }    class MyViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        var tvChat = itemView.findViewById<View>(R.id.tvChat) as TextView    }    init {        layoutInflater = LayoutInflater.from(context)    }}