package com.misit.abpenergy.Socketimport android.util.Logimport io.socket.client.IOimport io.socket.client.Socketimport io.socket.engineio.client.transports.WebSocketimport java.net.URISyntaxExceptionobject SocketHandler {        private lateinit var mSocket :Socket        private lateinit var opts:IO.Options        @Synchronized        fun setSocket(){            try {                opts = IO.Options()                opts?.transports = arrayOf(WebSocket.NAME)                mSocket = IO.socket("https://abpjobsite.com:3000")                Log.d("mSocket","${mSocket.id()}")            }catch (e:URISyntaxException){                Log.d("mSocket","${e.message}")            }        }         @Synchronized         fun getSocket():Socket{             return mSocket         }        @Synchronized        fun establishConnection():Socket {            return mSocket.connect()        }        @Synchronized        fun closeConnection() {            mSocket.disconnect()        }}