package com.misit.abpenergy.Utilsimport android.app.Activityimport android.content.Contextimport android.content.Intentimport android.view.Windowimport android.view.WindowManagerimport androidx.core.content.ContextCompatimport com.misit.abpenergy.Robject ConfigUtil {    fun changeColor(activity: Activity){        val window: Window = activity.window        window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS)        window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS)        window.statusBarColor = ContextCompat.getColor(activity, R.color.skyBlue)    }    fun resultIntent(activity: Activity){        val intent = Intent()        activity.setResult(Activity.RESULT_OK,intent)        activity.finish()    }}