package com.misit.abpenergy.Utilsimport android.app.Applicationimport io.realm.Realmimport io.realm.RealmConfigurationclass RealmConfig:Application() {    override fun onCreate() {        Realm.init(this@RealmConfig)        var realmConfig = RealmConfiguration.Builder().name("Abpenergy.db").schemaVersion(0)            .deleteRealmIfMigrationNeeded()            .build()        Realm.setDefaultConfiguration(realmConfig)        super.onCreate()    }}